import React, { useEffect, useState } from "react";
import { Typography, Link, CircularProgress, Button } from "@material-ui/core";
import { toFirstCharUppercase } from "./constants";
import axios from "axios";

const Recipe = (props) => {
  const { match, history } = props;
  const { params } = match;
  const { recipeID } = params;
  const [recipe, setRecipes] = useState(undefined);

  useEffect(() => {
    axios
      .get(`https://pokeapi.co/api/v2/pokemon/${pokemonId}/`)
      .then(function (response) {
        const { data } = response;
        setRecipes(data);
      })
      .catch(function (error) {
        setRecipes(false);
      });
  }, [recipeID]);

  const generateRecipeJSX = (recipe) => {
    const { name, id, species, height, weight, types, sprites } = recipe;
    const fullImageUrl = `https://pokeres.bastionbot.org/images/pokemon/${id}.png`;
    const { front_default } = sprites;
    return (
      <>
        <Typography variant="h1">
          {`${id}.`} {toFirstCharUppercase(name)}
          <img src={front_default} />
        </Typography>
        <img style={{ width: "300px", height: "300px" }} src={fullImageUrl} />
        <Typography>
          {"Species: "}
          <Link href={species.url}>{species.name} </Link>
        </Typography>
        <Typography>Height: {height} </Typography>
        <Typography>Weight: {weight} </Typography>
        <Typography variant="h6"> Types:</Typography>
        {types.map((typeInfo) => {
          const { type } = typeInfo;
          const { name } = type;
          return <Typography key={name}> {`${name}`}</Typography>;
        })}
      </>
    );
  };
  return (
    <>
      {recipe === undefined && <CircularProgress />}
      {recipe !== undefined && recipe && generateRecipeJSX(recipe)}
      {recipe === false && <Typography> Recipe not found</Typography>}
      {recipe !== undefined && (
        <Button variant="contained" onClick={() => history.push("/")}>
          Back
        </Button>
      )}
    </>
  );
};
export default Recipe;
