import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import {Button, Card, CardMedia, Grid, Paper, } from '@material-ui/core';

import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import Typography from '@material-ui/core/Typography';
import { useHistory } from "react-router-dom";

const useStyles = makeStyles({
  card_root: {
      minWidth: 650,
  },
  card_actions: {
      justifyContent: 'center',
  },
  media: {
      height: 0,
      paddingTop: '50%', 
  },
  media_icon: {
    height: '32px',
    width: '32px',
  },
  card_text: {
      textAlign: 'left',
      textDecoration: "none",
  },
  link: {
      textDecoration: "none",
  },

});

const Pokemon = props => {
  const { match } = props;
  const { params } = match;
  const { pokemonId } = params;
  const classes = useStyles();
  let history = useHistory();

  return (
    <Grid 
    container 
    xs={12} 
    spacing={0}
    alignItems="center" 
    justify="center"
    >
        <Grid item xs={4}>
            <CardActions className={classes.card_actions}>
                <Card className={classes.card_root} >
                  <CardContent>
                    <Grid container
                     xs={12}
                    spacing={0}
                    alignItems="center" 
                    justify="space-between"
                    >
                      <Grid item xs={4}>
                        <Typography variant="h3" component="h3" className={classes.card_text}>2</Typography>
                      </Grid>
                      <Grid item xs={4}>
                        <Typography variant="h3" component="h3" className={classes.card_text}>Pokemon title</Typography>
                      </Grid>
                      <Grid item xs={4}>
                      <CardMedia
                        className={classes.media_icon}
                        image="https://img.icons8.com/cotton/2x/small-axe.png"
                        title="pokemon"
                    />
                      </Grid>
                    </Grid>
                    <CardMedia
                        className={classes.media}
                        image="https://cdn.bulbagarden.net/upload/thumb/7/7e/006Charizard.png/1200px-006Charizard.png"
                        title="pokemon"
                    />

                    <Typography variant="h4" component="h4" className={classes.card_text}>Pokemon Info</Typography>
                    <Typography variant="h6" component="h6" className={classes.card_text}>Species: <span> charizard</span></Typography>
                    <Typography variant="h6" component="h6" className={classes.card_text}>Height: <span> 10</span></Typography>
                    <Typography variant="h6" component="h6" className={classes.card_text}>Weight: <span> 130</span></Typography>
                    <Typography variant="h5" component="h5" className={classes.card_text}>Types:</Typography>
                    <Typography variant="h6" component="h6" className={classes.card_text}>poisons</Typography>
                    <Button
                      variant="contained"
                      color="default"
                      onClick={() => history.goBack()}
                    >Back to PokeDex</Button>
                  </CardContent> 
                </Card>
            </CardActions>
        </Grid>

    </Grid>
  );
}

export default Pokemon;
