import React, { useEffect, useState } from "react";
import { Typography, Link, CircularProgress, Button } from "@material-ui/core";
import { toFirstCharUppercase } from "./constants";
import axios from "axios";
import mockData from './mockData';



const Recipe = (props) => {
  const { match, history } = props;
  const { params } = match;
  const { recipeID } = params;
  const [recipe, setRecipe] = useState(mockData);

  useEffect(() => {
    axios
      .get(`./Mock`)
      .then(function (response) {
        const { data } = response;
        setRecipe(data);
      })
      .catch(function (error) {
        setRecipe(false);
      });
  }, [recipeID]);


  const generateRecipeJSX = (recipe) => {
    const { name, id, ingredients, weight, image} = recipe;
    const fullImageUrl = {image};
    return (
      <>
        <Typography variant="h3">
        {toFirstCharUppercase(name)}
        </Typography>
        <img style={{ width: "300px", height: "300px" }} src={fullImageUrl} alt={fullImageUrl} />
        <Typography>Ingredients: {ingredients} </Typography>
        <Typography>Directions: {weight} </Typography>
      </>
    );
  };
  return (
    <>
      {recipe === undefined && <CircularProgress />}
      {recipe !== undefined && recipe && generateRecipeJSX(recipe)}
      {recipe === false && <Typography> Recipe not found</Typography>}
      {recipe !== undefined && (
        <Button variant="contained" onClick={() => history.push("/")}>
          Back
        </Button>
      )}
    </>
  );
};


export default Recipe;
