import React, { useEffect, useState } from "react";
import {
  Grid,
  Card,
  CardMedia,
  CardContent,
  Typography,
  CircularProgress,
  Toolbar,
  AppBar,
  TextField,
} from "@material-ui/core";
import { fade, makeStyles } from "@material-ui/core/styles";
import { toFirstCharUppercase } from "./constants";
import SearchIcon from "@material-ui/icons/Search";
import axios from "axios";
import mockData from './mockData';

const useStyles = makeStyles((theme) => ({
  pokedexContainer: {
    paddingTop: "20px",
    paddingLeft: "50px",
    paddingRight: "50px",
  },
  cardMedia: {
    margin: "auto",
  },
  cardContent: {
    textAlign: "center",
  },
  searchContainer: {
    display: "flex",
    backgroundColor: fade(theme.palette.common.white, 0.15),
    paddingLeft: "20px",
    paddingRight: "20px",
    marginTop: "5px",
    marginBottom: "5px",
  },
  searchIcon: {
    alignSelf: "flex-end",
    marginBottom: "5px",
  },
  searchInput: {
    width: "200px",
    margin: "5px",
  },
}));

const GridView = (props) => {
  const classes = useStyles();
  const { history } = props;
  const [recipeData, setRecipeData] = useState(mockData);
  const [filter, setFilter] = useState("");

  useEffect(() => {
    axios
      .get(`./Mock`)
      .then(function (response) {
        const { data } = response;
        const { results } = data;
        const newRecipeData = {};
        results.forEach((pokemon, index) => {
          newRecipeData[index + 1] = {
            id: index + 1,
            name: pokemon.name,
            sprite: `https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/${
              index + 1
            }.png`,
          };
        });
        setRecipeData(newRecipeData);
      });
  }, []);

  const handleSearchChange = (e) => {
    setFilter(e.target.value.toLowerCase().match(t.toLowerCase() ));
  };

  const getRecipeCard = (recipeId) => {
    const { id, name, image } = recipeData[recipeId];
    return (
      <Grid item xs={4} key={recipeId}>
        <Card onClick={() => history.push(`/${id}`)}>
          <CardMedia
            className={classes.cardMedia}
            image={image}
            style={{ width: "130px", height: "130px" }}
          />
          <CardContent className={classes.cardContent}>
            <Typography>{`${id}. ${toFirstCharUppercase(name)}`}</Typography>
          </CardContent>
        </Card>
      </Grid>
    );
  };

  return (
    <>
      <AppBar position="static">
        <Toolbar>
          <div className={classes.searchContainer}>
            <SearchIcon className={classes.searchIcon} />
            <TextField
              className={classes.searchInput}
              onChange={handleSearchChange}
              label="Recipes"
              variant="standard"
            />
          </div>
        </Toolbar>
      </AppBar>
      {recipeData ? (
        <Grid container spacing={2} className={classes.pokedexContainer}>
          {Object.keys(recipeData).map(
            (recipeId) =>
              recipeData[recipeId].name.includes(filter) &&
              getRecipeCard(recipeId)
          )}
        </Grid>
      ) : (
        <CircularProgress />
      )}
    </>
  );
};
export default GridView;
