import React, {useState} from 'react';
import mockData from './mockData';
import { Typography } from '@material-ui/core';

const Recipe = (props) => {
  const {match}  = props;
  const {params} = match;
  const {recipeId} = params;
  const [recipe, setRecipe] = useState(mockData[`${recipeId}`]);

  const generateRecipeJSX = () => {
    const {name, id, height, weight} = recipeId;
    const fullImageUrl = 'https://graphics8.nytimes.com/images/2009/09/11/dining/10mini600.1.jpg';

    return (
      <Typography variant="h3">
        {`${id}.`} {toFirstCharUppercase(name)}
        <img src={fullImageUrl} />;
      </Typography>
    )

  }
}


export default Recipe;
